mapper.update:
update t_baby_apply
set user_code = #{userCode,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  apply_status = #{applyStatus,jdbcType=VARCHAR},
  require_code = #{requireCode,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, user_code, create_time, apply_status, require_code
from t_baby_apply
where id = #{id,jdbcType=BIGINT}

mapper.update:
update t_baby_apply set 

  user_code = #{userCode,jdbcType=VARCHAR},


  create_time = #{createTime,jdbcType=TIMESTAMP},


  apply_status = #{applyStatus,jdbcType=VARCHAR},


  require_code = #{requireCode,jdbcType=VARCHAR}

 where id = #{id,jdbcType=BIGINT}

mapper.select:
select * from t_baby_apply where 1=1 

  and id = #{id,jdbcType=BIGINT}


  and user_code = #{userCode,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and apply_status = #{applyStatus,jdbcType=VARCHAR}


  and require_code = #{requireCode,jdbcType=VARCHAR}
