mapper.select:
select id, payment_type, amount, create_time, payment_status, last_update_time, busi_payment_code1
from t_baby_payment_record
where id = #{id,jdbcType=BIGINT}

select * from t_baby_payment_record where 1=1 

  and id = #{id,jdbcType=BIGINT}


  and payment_type = #{paymentType,jdbcType=VARCHAR}


  and amount = #{amount,jdbcType=REAL}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and payment_status = #{paymentStatus,jdbcType=VARCHAR}


  and last_update_time = #{lastUpdateTime,jdbcType=TIMESTAMP}


  and busi_payment_code1 = #{busiPaymentCode1,jdbcType=VARCHAR}
