set item_code = #{itemCode,jdbcType=VARCHAR},
  item_name = #{itemName,jdbcType=VARCHAR},
  item_price = #{itemPrice,jdbcType=REAL},
  require_code = #{requireCode,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, item_code, item_name, item_price, require_code
from t_baby_require_items
where id = #{id,jdbcType=BIGINT}

mapper.update:
update t_baby_require_items set 

  item_code = #{itemCode,jdbcType=VARCHAR},


  item_name = #{itemName,jdbcType=VARCHAR},


  item_price = #{itemPrice,jdbcType=REAL},


  require_code = #{requireCode,jdbcType=VARCHAR}

 where id = #{id,jdbcType=BIGINT}

mapper.select:
select * from t_baby_require_items where 1=1 

  and id = #{id,jdbcType=BIGINT}


  and item_code = #{itemCode,jdbcType=VARCHAR}


  and item_name = #{itemName,jdbcType=VARCHAR}


  and item_price = #{itemPrice,jdbcType=REAL}


  and require_code = #{requireCode,jdbcType=VARCHAR}
