  and id = #{id,jdbcType=BIGINT}


  and require_code = #{requireCode,jdbcType=VARCHAR}


  and send_user_code = #{sendUserCode,jdbcType=VARCHAR}


  and get_user_code = #{getUserCode,jdbcType=VARCHAR}


  and task_status = #{taskStatus,jdbcType=VARCHAR}


