from t_baby_task_record
where id = #{id,jdbcType=BIGINT}

mapper.update:
update t_baby_task_record set 

  step_content = #{stepContent,jdbcType=VARCHAR},


  done_time = #{doneTime,jdbcType=TIMESTAMP},


  create_time = #{createTime,jdbcType=TIMESTAMP},


  task_code = #{taskCode,jdbcType=VARCHAR}

 where id = #{id,jdbcType=BIGINT}

mapper.select:
select * from t_baby_task_record where 1=1 

  and id = #{id,jdbcType=BIGINT}


  and step_content = #{stepContent,jdbcType=VARCHAR}


  and done_time = #{doneTime,jdbcType=TIMESTAMP}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and task_code = #{taskCode,jdbcType=VARCHAR}
