mapper.update:
update t_baby_time_price
set price = #{price,jdbcType=DOUBLE},
  last_update_time = #{lastUpdateTime,jdbcType=TIMESTAMP},
  price_code = #{priceCode,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, price, last_update_time, price_code
from t_baby_time_price
where id = #{id,jdbcType=BIGINT}

mapper.update:
update t_baby_time_price set 

  price = #{price,jdbcType=DOUBLE},


  last_update_time = #{lastUpdateTime,jdbcType=TIMESTAMP},


  price_code = #{priceCode,jdbcType=VARCHAR}

 where id = #{id,jdbcType=BIGINT}

mapper.select:
select * from t_baby_time_price where 1=1 

  and id = #{id,jdbcType=BIGINT}


  and price = #{price,jdbcType=DOUBLE}


  and last_update_time = #{lastUpdateTime,jdbcType=TIMESTAMP}


  and price_code = #{priceCode,jdbcType=VARCHAR}
